import React, { useState } from 'react'

const cartContext = React.createContext({
   items: [],
   totalAmount: 0,
   addItem: (item) => {},
   removeItem: (id) => {}

})

export const ContextProvider = ({children}) => {

    return (
       <cartContext.Provider>
          {children}
       </cartContext.Provider>
    );
}

export default cartContext;
