import React, { useContext } from 'react'
import classes from '../styles/MealItem.module.css';
import cartContext from './ContextProvider';
import { MealItemForm } from './MealItemForm';

export const MealItem = () => {
const Dishes = useContext({cartContext});

    return (
        <>
        {Dishes.menu.map(dish => {
            return (
        <div className={classes.meal} key={dish.id}>
            <div>
            <h3> {dish.name}</h3>
            <div className={classes.description}>Here goes the description</div>
            <div className={classes.price}>{`$${dish.price}`}</div>
            </div>
            <MealItemForm/>
            <div className={classes.meal}></div>
        </div>
        )})}
        </>
    )
}
