import React, { useContext, useState } from 'react'
import classes from '../styles/MealItemForm.module.css';
import cartContext from '../components/ContextProvider';
import { Input } from './Input';

export const MealItemForm = (props) => {
    const [value,setValues] = useState(0);
    const ctx = useContext(cartContext);

    const addToCart = (event) => {
        event.preventDefault();
        console.log(value)

        for (var int=0; int <= value; int++){
            ctx.cart.push(props.onObject)
        }
        console.log(ctx.cart)
    }

    const changinValue = (event) => {
        setValues(event.target.value);
    }

    return (
        <form className={classes.form} onSubmit={addToCart}>
            <Input onAmount={changinValue}/>
            <button>+ Add</button>
        </form>
    )
}
