import React, { useState } from 'react'

const initialState = {
   cart: [],
   menu: [
      {
         name: "Fish",
         description: "Something delicious oh yes",
         price: 19.9,
         id: 1,
      },
      {
         name: "Meat",
         description: "Something delicious oh yes",
         price: 17.9,
         id: 2,
      },
      {
         name: "Chicken",
         description: "Something delicious oh yes",
         price: 15.9,
         id: 3,
      },
   ],
};

const cartContext = React.createContext({
})

export const ContextProvider = ({children}) => {

    const reRender = () => {
        const amount = cartContext.cart.length;
    }

    return (
       <cartContext.Provider
          value={{
             cart: [],
             menu: [
                {
                   name: "Fish",
                   description: "Something delicious oh yes",
                   price: 19.9,
                   id: 1,
                },
                {
                   name: "Meat",
                   description: "Something delicious oh yes",
                   price: 17.9,
                   id: 2,
                },
                {
                   name: "Chicken",
                   description: "Something delicious oh yes",
                   price: 15.9,
                   id: 3,
                },
             ],
             onRender: reRender
          }}
       >
          {children}
       </cartContext.Provider>
    );
}

export default cartContext;
