        event.preventDefault();
        console.log()
    }

    const changinValue = (event) => {
        console.log(event.target.value)
    }


    return (
        <form className={classes.form} onSubmit={addToCart}>
            <Input onAmount={changinValue}/>
            <button>+ Add</button>
        </form>
    )
}
