        event.preventDefault();
    }

    const assigner = (event) => {
        var dataValue = event.target.value;
        console.log(dataValue);
    }

    const trigger = () => {
        var dataValue = assigner();
        console.log(dataValue)
    }

    return (
        <form className={classes.form} onSubmit={addToCart}>
            <Input onRead={assigner}/>
            <button>+ Add</button>
        </form>
    )
}
