        const value = event.target.value
        console.log(value)
    },value)

    const addToCart = (event) => {
        event.preventDefault();
        console.log()
    }


    return (
        <form className={classes.form} onSubmit={addToCart}>
            <Input onAmount={setValue}/>
            <button>+ Add</button>
        </form>
    )
}
