import React, { useContext, useRef, useState } from 'react'
import classes from '../styles/MealItemForm.module.css';
import cartContext from '../store/ContextProvider';
import { Input } from './Input';

export const MealItemForm = (props) => {

    const amountInputRef = useRef();
    const [amountIsValid, setAmountIsValid] = useState();

    const addToCart = (event) => {
        event.preventDefault();

        const enteredAmount = +amountInputRef.current.value;
        if (
           enteredAmount.trim().length === 0 ||
           enteredAmount < 1 ||
           enteredAmount > 5
        ) {
            setAmountIsValid(false); 
            return;
        }
            
        props.onAddToCart(enteredAmount);
    }

    return (
       <form className={classes.form} onSubmit={addToCart}>
          <Input
             ref={amountInputRef}
             onAmount={changinValue}
             label="Amount"
             input={{
                id: "amount" + props.id,
                type: "number",
                min: "1",
                max: "5",
                defaultValue: "1",
             }}
          />
          <button>+ Add</button>
          {!amountIsValid && <p>Please, entered a valid amount (1-5)</p>}
       </form>
    );
}
