import React, { useRef } from 'react'
import classes from '../styles/MealItemForm.module.css';
import { Input } from './Input';

export const MealItemForm = () => {

    const addToCart = (event) => {
        event.preventDefault();
    }

    const amount = useRef();
    const assigner = (event) => {
        dataValue = event.target.value;
        console.log(dataValue);
    }

    const trigger = () => {
        console.log(amount)
    }

    return (
        <form className={classes.form} onSubmit={addToCart}>
            <Input ref={amount} onRead={assigner}/>
            <button onClick={trigger}>+ Add</button>
        </form>
    )
}
